mapper.update:
UPDATE QABOARD SET 
	qaBoardAnswer = #{qaBoardAnswer}
		WHERE qaBoardNo = #{qaBoardNo}

UPDATE QABOARD SET 
	qaBoardTitle = #{qaBoardTitle},
	qaBoardContents = #{qaBoardContents},
	category_id = #{category_id}
		WHERE qaBoardNo = #{qaBoardNo}

UPDATE QABOARD SET 
	views = views + 1
		WHERE qaBoardNo = #{qaBoardNo}
